import React, {useEffect, useState } from 'react'
import './App.css';
import Search from "./components/Search";
import CartList from './components/CartList';
import Home from './components/Home';
import axios from 'axios'
import CarDetails from './components/CarDetails';


const App = () => {
const [menuView, setMenuView] = useState(false);
const [view,setView] = useState("Home");
const [data, setData]=useState([])
const [USV, setUSV]=useState([])
const [SUPERCAR, setSUPERCAR]=useState([])
const [CLASS, setCLASS]=useState([])
const [trigger, setTrigger]=useState(false)
const [oneProduct, setOneProduct] = useState({});
const [card , setCard]=useState([])

/////////////////////////////////////// for getting the data //////////////////////////////////////
useEffect(()=>{
axios.get("http://localhost:5000/api/products")
.then((resp)=>{
  setUSV(resp.data.filter((e,i)=>e.category==="USV"))
  setSUPERCAR(resp.data.filter((e,i)=>e.category==="SUPERCAR"))
  setCLASS(resp.data.filter((e,i)=>e.category==="CLASS"))
  setData(resp.data)
  })
  .catch((err)=>console.log(err))
},[trigger])

const toggleMenu = ()=> {
  setMenuView(!menuView)
}
const switchView = (x) => {
  setView(x)
}


///////////////////////////////////////// for post details ////////////////////////////////////////////
const stal = (x) => {
  setOneProduct(x);
  switchView("Details"); 
};
//////////////////////////////////////// for add,delete and check to cart ///////////////////////////////////////
const cartp =(x)=>{
  setCard([...card,x]);
 ; 
}


const removeFromCart = (index) => {
  const updatedCart = [...card];
  updatedCart.splice(index, 1);
  setCard(updatedCart);
};

const Checkout = () => {
  alert("Thank you from us")
  setCard([]); 
};

///////////////////////////////////////// for search ////////////////////////////////////////////

const search = (input) => {
if(!input){
  setTrigger(!trigger)
}
  const filtersss = data.filter((e) =>
      e.name.toLowerCase().includes(input.toLowerCase())
  );
  setData(filtersss);
};
///////////////////////////////////////////for update /////////////////////////////////////////





  return (
    <div className="App">
        <div className="nav">
          <span className="logo" onClick={()=>{switchView ("Home") 
           setTrigger(!trigger); }}>MANSORY</span>

          { view ==="Home" &&<Search search={search}/>}
        { view ==="Home" && <span className="items" onClick={toggleMenu}>

            CATEGORIES

          </span>}
          <span className="items" onClick={()=>switchView ("cart")}>

            CART
          </span>
        </div>
       {menuView && <div className="menu">
            <span className='menu-item' onClick={()=>{setData(USV)}}>USV</span>
            <span className='menu-item' onClick={()=>{setData(CLASS)}}>CLASS</span>
            <span className='menu-item' onClick={()=>{setData(SUPERCAR)}}>SUPERCAR</span>
          </div>}
          {view ==="Home" && <Home product={data} stal={stal}  cartp={cartp}  trigger={trigger} setTrigger={setTrigger}/>}
          {view ==="cart" && <CartList product={card} removeFromCart={removeFromCart} Checkout={Checkout} />}  
          {view === "Details" &&  <CarDetails product={oneProduct} />}


    </div>
  );
}

export default App;
